function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    // Get field values
    var choice = g_form.getValue('u_capex_or_opex');
    var opexValue = g_form.getValue('u_opex_value');
	var capexValue =g_form.getValue('u_capex_value');

    // Calculate and set u_opex_value only if choice is 'Capex & Opex'
    if (choice === 'Capex & Opex') {


        var capexValue1 = parseFloat(g_form.getValue('u_capex_value').replace(/,/g, '')) || 0;
        var opexValue1 = parseFloat(g_form.getValue('u_opex_value').replace(/,/g, '')) || 0;
        var newTotalValue = capexValue1 + opexValue1;
        var formatted = newTotalValue.toLocaleString('en-US');

        g_form.setValue('u_value', formatted);

    } else if(choice == 'Capex') {
        // Clear u_opex_value if choice is not 'Capex & Opex'
		var formatted1 = (capexValue, 10).toLocaleString('en-US');
        g_form.setValue('u_value', capexValue);
    } else if(choice == 'Opex'){
		g_form.clearValue('capexValue', true);
	}

    //Type appropriate comment here, and begin script below

}
