sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var capex = g_form.getValue('u_capex') == 'true';
   var opex = g_form.getValue('u_opex') == 'true';
   var totalValue = parseInt(g_form.getValue('u_total_value')) ;
   var capexValue = parseInt(g_form.getValue('u_capex_value')) ;
   var opexValue = parseInt(g_form.getValue('u_opex_value')) ;

   // Case 1: Only u_capex is True
   if (capex && !opex) {
      g_form.setValue('u_capex_value', totalValue);
    //   g_form.setValue('u_opex_value', 0);
   }
   // Case 2: Only u_opex is True
   else if (opex && !capex) {
      g_form.setValue('u_opex_value', totalValue);
    //   g_form.setValue('u_capex_value', 0);
   }
   // Case 3: Both u_capex and u_opex are True
   else if (capex && opex) {
      // If u_capex_value is non-zero, calculate u_opex_value
      if (capexValue > 0) {
         g_form.setValue('u_opex_value', totalValue - capexValue);
      }
      // If u_opex_value is non-zero, calculate u_capex_value
      else if (opexValue > 0) {
         g_form.setValue('u_capex_value', totalValue - opexValue);
      }
      // If neither is set, default to splitting or setting one
      else {
         g_form.setValue('u_capex_value', totalValue);
         g_form.setValue('u_opex_value', 0);
      }
   }
   // Case 4: Both are False
   else {
      g_form.setValue('u_capex_value', 0);
      g_form.setValue('u_opex_value', 0);
   }
}
