sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var capex = g_form.getValue('u_capex') == 'true';
   var opex = g_form.getValue('u_opex') == 'true';
   var totalValue = g_form.getValue('u_total_value') ;
   var capexValue = g_form.getValue('u_capex_value');

   // If u_opex is True and u_capex is True, calculate u_opex_value
   if (capex && opex) {

      	g_form.setValue('u_opex_value', totalValue - capexValue);
   }
   // If only u_capex is True, u_capex_value should equal u_total_value
   else if (capex && !opex) {
      g_form.setValue('u_capex_value', totalValue);
	//   g_form.setValue('u_opex_value', 0)
   }
    else if (!opex && !capex){
	g_form.clearValue('u_capex_value')
	g_form.clearValue('u_opex_value')
   } 

}
