sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var choice = g_form.getValue('u_capex_or_opex');
    var opexValue = g_form.getValue('u_opex_value');
    var capexValue = g_form.getValue('u_capex_value');


    if (choice == 'Capex') {
		g_form.clearValue('u_value', true);
		 g_form.setReadOnly('u_value', true);
        // Clear the message.
        g_form.clearMessages();
		g_form.setMandatory('u_opex_value', false);
        g_form.setVisible('u_opex_value', false);


        // Display a message
        g_form.addInfoMessage("You must fill in the Capex Value Field");
        g_form.setVisible('u_capex_value', true);
        g_form.setMandatory('u_capex_value', true);

        g_form.setValue('u_value', capexValue);

        g_form.clearValue('u_opex_value');
        // g_form.setDisplay('u_opex_value', false);
    }
    if (choice == 'Opex') {
		g_form.clearValue('u_value', true);
		g_form.setReadOnly('u_value', true);
        // Clear the message.
        g_form.clearMessages();
		g_form.setMandatory('u_capex_value', false);
        g_form.setVisible('u_capex_value', false);


        // Display a message
        g_form.addInfoMessage("You must fill in the Opex Value Field");
        g_form.setVisible('u_opex_value', true);
        g_form.setMandatory('u_opex_value', true);

        g_form.setValue('u_value', opexValue);

        g_form.clearValue('u_capex_value');
        // g_form.setDisplay('u_capex_value', false);
    }

    // else if (choice == 'Opex') {
    // 	// Clear the message.
    // 	g_form.clearMessages();
    // 	g_form.setVisible('u_capex_value', false);
    // 	g_form.setVisible('u_opex_value', true);

    // 	// Display a message
    // 	g_form.addInfoMessage("You must fill in the Opex Value Field");
    // 	g_form.setMandatory('u_opex_value',true);
    // 	g_form.setMandatory('u_capex_value',false);


    //     g_form.setValue('u_value', opexValue);
    //     g_form.setReadOnly('u_value', true);

    //     // g_form.setDisplay('u_capex_value', false);
    //     g_form.clearValue('u_capex_value');
    // } 
    else if (choice == 'Capex & Opex') {
		g_form.clearValue('u_value', true);
		g_form.setReadOnly('u_value', true);
        g_form.setVisible('u_capex_value', true);
        g_form.setVisible('u_opex_value', true);
        // Clear the message.
        g_form.clearMessages();
        // g_form.setMandatory('u_opex_value',false);
        // display a message
        g_form.addInfoMessage("You must fill both the Capex Value and Opex Value");

        // Clear the values
        g_form.clearValue('u_capex_value');
        g_form.clearValue('u_opex_value');

        // set mandatory
        g_form.setMandatory('u_capex_value', true);
        g_form.setMandatory('u_opex_value', true);
        g_form.setValue('u_value', capexValue + opexValue);
    } else {
        g_form.clearMessages();
        g_form.setReadOnly('u_value', true);

        // make the Opex-Capex value invisible.
        g_form.setVisible('u_capex_value', false);
        g_form.setVisible('u_opex_value', false);
    }



    //Type appropriate comment here, and begin script below

}
